document.addEventListener("DOMContentLoaded",function(){const e=[].slice.call(document.querySelectorAll(".lazy-load-section"));if("IntersectionObserver"in window){let t=new IntersectionObserver(function(e,t){e.forEach(function(e){e.isIntersecting&&(e.target.innerHTML=`<h2>${e.target.dataset.content} Content</h2><p>This section has now been loaded lazily.</p>`,t.unobserve(e.target))})},{threshold:.5});e.forEach(function(e){t.observe(e)})}})