document.addEventListener("DOMContentLoaded",()=>{const e=new IntersectionObserver(t=>{t.forEach(t=>{if(t.isIntersecting){const s=t.target,o=s.dataset.src,i=s.dataset.title||"YouTube video player",n=document.createElement("iframe");n.src=o,n.width="100%",n.height="315",n.allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture",n.allowFullscreen=!0,n.frameBorder="0",n.title=i,s.innerHTML="",s.appendChild(n),e.unobserve(s)}})});document.querySelectorAll(".lazy-iframe").forEach(t=>e.observe(t))})