document.addEventListener("DOMContentLoaded",function(){let t=100;const e=document.querySelector(".bottom-nav");window.innerWidth<=600&&e.classList.add("show"),window.addEventListener("scroll",function(){const n=window.pageYOffset||document.documentElement.scrollTop;n>t?(e.classList.remove("show"),e.classList.add("hide")):(e.classList.remove("hide"),e.classList.add("show")),t=n<=0?0:n})})