document.addEventListener("DOMContentLoaded",function(){const e=document.getElementById("scrollMenu"),t=document.querySelector(".scroll-left"),n=document.querySelector(".scroll-right");function s(){e.scrollLeft>0?t.classList.add("show"):t.classList.remove("show"),e.scrollLeft+e.clientWidth<e.scrollWidth?n.classList.add("show"):n.classList.remove("show")}function o(t){const n=e.clientWidth/2;e.scrollBy({left:t==="left"?-n:n,behavior:"smooth"})}t.addEventListener("click",()=>o("left")),n.addEventListener("click",()=>o("right")),e.addEventListener("scroll",s),window.addEventListener("resize",s),s()})