function populateInputAndSearch(){var e=document.getElementById("categoryFilter"),t=document.getElementById("searchBar");t.value=e.value,searchTable()}function searchTable(){var e,t,n,o,i=document.getElementById("searchBar"),a=i.value.toLowerCase(),r=document.getElementById("formsTable"),s=r.getElementsByTagName("tr");for(e=1;e<s.length;e++){s[e].style.display="none",n=s[e].getElementsByTagName("td");for(t=0;t<n.length;t++)if(n[t]&&(o=n[t].textContent||n[t].innerText,o.toLowerCase().indexOf(a)>-1)){s[e].style.display="";break}}}