document.getElementById("searchButton").addEventListener("click",function(){const e=document.getElementById("searchInput").value;e&&(updateURL(e),search(e))});function updateURL(e){const t=`#gsc.tab=0&gsc.sort=&gsc.q=${encodeURIComponent(e)}`;window.location.hash=t}function readQuery(){const t=window.location.hash,n=new URLSearchParams(t.substring(1)),e=n.get("gsc.q");e&&(document.getElementById("searchInput").value=e,search(e))}window.onload=readQuery