const openBtn=document.getElementById("openSidebar"),closeBtn=document.getElementById("closeSidebar"),sidebar=document.getElementById("leftSidebar"),overlay=document.getElementById("sidebarOverlay");openBtn.addEventListener("click",()=>{sidebar.classList.add("active"),overlay.classList.add("active")}),closeBtn.addEventListener("click",()=>{sidebar.classList.remove("active"),overlay.classList.remove("active")}),overlay.addEventListener("click",()=>{sidebar.classList.remove("active"),overlay.classList.remove("active")})