document.getElementById("search-button").addEventListener("click",function(){const e=document.getElementById("search-input").value;e&&(updateURL(e),search(e))});function updateURL(e){const t=`#gsc.tab=0&gsc.sort=&gsc.q=${encodeURIComponent(e)}`;window.location.hash=t}function readQuery(){const t=window.location.hash,n=new URLSearchParams(t.substring(1)),e=n.get("gsc.q");e&&(document.getElementById("search-input").value=e,search(e))}window.onload=readQuery,document.querySelectorAll(".dynamic-link").forEach(e=>{e.addEventListener("click",function(e){e.preventDefault();const t=this.getAttribute("data-search"),n=`?gsc.q=${encodeURIComponent(t)}`;window.history.pushState({},"",n),document.querySelector("input.gsc-input").value=t,document.querySelector("button.gsc-search-button").click()})});function removeGSCtabHash(){location.hash.startsWith("#gsc.tab=")&&history.replaceState(null,document.title,window.location.pathname+window.location.search)}window.addEventListener("load",function(){setTimeout(removeGSCtabHash,100)}),window.addEventListener("hashchange",function(){setTimeout(removeGSCtabHash,200)})